<?php get_header() ?>        <?php if(is_front_page()) : ?>            <?php if($image = carbon_get_theme_option( 'common_pupuga_configuration_home_image')): ?>                <div class="general-section__top general-section__top--image">                    <?php echo wp_get_attachment_image($image, 'full') ?>                </div>            <?php endif ?>        <?php elseif(Pupuga\Core\Base\Common::app()->get('removeTitle') !== true) : ?>            <div class="skeleton skeleton--center">                <h1 class="general-title"><?php the_title() ?></h1>            </div>        <?php endif ?>        <div class="skeleton skeleton--center">        <?php if(Pupuga\Core\Base\Common::app()->get('removeTitle') !== true) : ?>            <div class="general-content">        <?php endif ?>                <?php echo Pupuga\Libs\Data\Html::cleanEmptyTagsHtml(apply_filters('the_content', get_the_content())) ?>        <?php if(Pupuga\Core\Base\Common::app()->get('removeTitle') !== true) : ?>            </div>        <?php endif ?>        </div><?php get_footer() ?>